import axios from 'axios';
import React, { useState } from 'react';
import './profileForm.css';

function ProfileForm({ user }) {
  const [password, setPassword] = useState(null);
  const [profilePic, setProfilePic] = useState(null);
  const [coverPic, setCoverPic] = useState(null);

  const submitHandler = async (e) => {
    e.preventDefault();
    const newUser = {
      userId: user._id,
      password: password,
      coverPicture: '',
      profilePicture: '',
    };
    if (profilePic || coverPic) {
      const data = new FormData();
      const coverName = Date.now() + coverPic.name;
      const profileName = Date.now() + profilePic.name;
      data.append('name', coverName);
      data.append('name', profileName);
      data.append('file', profilePic);
      data.append('file', coverPic);

      newUser.profilePicture = coverName;
      newUser.coverPicture = profileName;
      try {
        await axios.post('/upload', data);
      } catch (err) {
        console.log(err);
      }
    }
    try {
      await axios.put('/posts', newUser);
      window.location.reload();
    } catch (err) {}
  };

  return (
    <form onSubmit={(e) => submitHandle(e)}>
      <div className='uploadInput'>
        <label>
          <span>Change profile picture</span>
          <input
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setProfilePic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          <span>Change cover picture</span>
          <input
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setCoverPic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>Change information</label>
      </div>
      <div className='uploadInput'>
        <label id='passwordLabel'>Enter password to save changes</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type='password'
        ></input>
      </div>
      <button className='submitButton'>Save changes</button>
    </form>
  );
}

export default ProfileForm;
