import React, { useContext, useState } from 'react';
import Header from '../../components/header/Header';
import Conversation from '../../components/conversations/Conversation';
import './chat.css';
import Message from '../../components/message/Message';
import ChatOnline from '../../components/chatOnline/ChatOnline';
import { AuthContext } from '../../context/AuthContext';
import { useEffect } from 'react';
import axios from 'axios';
import useFetch from '../../hooks/useFetch';

export default function Chat() {
  const [currentChat, setCurrentChat] = useState(null);
  const { user } = useContext(AuthContext);
  const { data: conversations } = useFetch('/conversations/' + user._id);
  const { data: messages } = useFetch('/messages/' + currentChat?._id);
  console.log(currentChat?._id, messages?.conversationId);
  return (
    <>
      <Header />
      <div className='chat'>
        <div className='chatMenu'>
          <div className='chatMenuWrapper'>
            <input placeholder='Search for friends' className='chatMenuInput' />
            {conversations.map((conv) => (
              <div onClick={() => setCurrentChat(conv)} key={conv._id}>
                <Conversation conversation={conv} currentUser={user} />
              </div>
            ))}
          </div>
        </div>
        <div className='chatBox'>
          <div className='chatBoxWrapper'>
            {currentChat ? (
              <>
                <div className='chatBoxTop'>
                  {messages?.map((message) => {
                    if (messages.conversationId === currentChat._id) {
                      console.log(message);
                    }
                  })}
                </div>
                <div className='chatBoxBottom'>
                  <textarea
                    className='chatMessageInput'
                    placeholder='Write a message...'
                  ></textarea>
                  <button className='chatSubmitButton'>Send</button>
                </div>{' '}
              </>
            ) : (
              <span className='noConversationText'>
                Open a conversation to start a chat
              </span>
            )}
          </div>
        </div>
        <div className='chatOnline'>
          <div className='chatOnlineWrapper'>
            <ChatOnline />
            <ChatOnline />
            <ChatOnline />
          </div>
        </div>
      </div>
    </>
  );
}
