    data.append('name', fileName);
    data.append('file', file);
  };

  return (
    <form>
      <div className='uploadInput'>
        <label>
          <span>
            {!profilePic ? (
              'Change profile picture'
            ) : (
              <button onClick={uploadProfile} type='button'>
                upload
              </button>
            )}
          </span>
          <input
            name='cover'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setProfilePic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          <span>Change cover picture</span>
          <input
            name='profile'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setCoverPic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>Change information</label>
      </div>
      <div className='uploadInput'>
        <label id='passwordLabel'>Enter password to save changes</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type='password'
        ></input>
      </div>
      <button type='submit' className='submitButton'>
        Save changes
      </button>
    </form>
  );
}

export default ProfileForm;
