import axios from 'axios';
import { useState } from 'react';
import './profileForm.css';

function ProfileForm({ user }) {
  const [password, setPassword] = useState(null);
  const [profilePic, setProfilePic] = useState('');
  const [coverPic, setCoverPic] = useState('');

  const submitHandler = async (e) => {
    e.preventDefault();
    const newUser = {
      userId: user._id,
      password: password,
      coverPicture: '',
      profilePicture: '',
    };

    const cover = {};
    const coverName = 'cover' + coverPic.name;
    cover.append('name', coverName);
    cover.append('cover', coverPic);

    const profile = new FormData();
    const profileName = 'profile' + profilePic.name;
    cover.append('name', profileName);
    cover.append('profile', profilePic);

    try {
      await axios.post('/upload', cover);
    } catch (err) {
      console.log(err);
    }
    newUser.coverPicture = coverName;
    newUser.profilePicture = profileName;
    try {
      await axios.put('/users/' + user._id, newUser);
      window.location.reload();
    } catch (err) {}
  };

  return (
    <form onSubmit={(e) => submitHandler(e)}>
      <div className='uploadInput'>
        <label>
          <span>Change profile picture</span>
          <input
            name='cover'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setProfilePic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          <span>Change cover picture</span>
          <input
            name='profile'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setCoverPic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>Change information</label>
      </div>
      <div className='uploadInput'>
        <label id='passwordLabel'>Enter password to save changes</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type='password'
        ></input>
      </div>
      <button type='submit' className='submitButton'>
        Save changes
      </button>
    </form>
  );
}

export default ProfileForm;
