import axios from 'axios';
import { useRef } from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import './profileForm.css';

function ProfileForm({ user }) {
  const [password, setPassword] = useState(false);
  const [profilePic, setProfilePic] = useState('');
  const [coverPic, setCoverPic] = useState('');
  const [update, setUpdate] = useState(false);
  const [error, setError] = useState('Enter password to save changes');
  const [info, setInfo] = useState({
    desc: '',
    city: '',
    from: '',
    relationship: '',
  });
  const [newUser, setNewUser] = useState({
    userId: user._id,
    password: null,
    profilePicture: null,
    coverPicture: null,
  });

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(user));
  }, [user]);

  //Profile picture
  const uploadProfile = async (e) => {
    e.preventDefault();

    const data = new FormData();
    const fileName = Date.now() + profilePic.name;
    data.append('name', fileName);
    data.append('file', profilePic);

    setNewUser({
      ...newUser,
      profilePicture: fileName,
    });
    try {
      await axios.post('/upload', data);
    } catch (err) {}
  };

  //Cover update
  const uploadCover = async (e) => {
    e.preventDefault();

    const data = new FormData();
    const fileName = Date.now() + coverPic.name;
    data.append('name', fileName);
    data.append('file', coverPic);
    setNewUser({
      ...newUser,
      coverPicture: fileName,
    });
    try {
      await axios.post('/upload', data);
    } catch (err) {}
  };
  console.log(newUser);

  //Submit

  const submitHandler = async (e) => {
    e.preventDefault();

    let updatedUser = { ...newUser };
    updatedUser.userId = user._id;

    if (!updatedUser.profilePicture) {
      updatedUser.profilePicture = user.profilePicture;
    }
    if (!updatedUser.coverPicture) {
      updatedUser.coverPicture = user.coverPicture;
    }
    Object.keys(info).map((key) => {
      if (info[key].length > 0) {
        updatedUser[key] = info[key];
      }
    });

    try {
      await axios.put('/users/' + user._id, updatedUser);
    } catch (error) {
      console.log(error);
    }

    localStorage.setItem('user', JSON.stringify(user));
    window.location.reload();
  };

  return (
    <form onSubmit={(e) => submitHandler(e)}>
      <div className='uploadInput'>
        <label>
          <span>
            {!profilePic ? (
              'Change profile picture'
            ) : (
              <button onClick={(e) => uploadProfile(e)} type='button'>
                upload
              </button>
            )}
          </span>
          <input
            name='cover'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setProfilePic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          <span>
            {!coverPic ? (
              'Change cover picture'
            ) : (
              <button onClick={(e) => uploadCover(e)} type='button'>
                upload
              </button>
            )}
          </span>
          <input
            name='profile'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setCoverPic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          {!update ? (
            <span onClick={() => setUpdate(true)}>Change information</span>
          ) : (
            <div>
              <label>Description</label>
              <input
                onChange={(e) =>
                  setInfo({
                    ...info,
                    desc: e.target.value,
                  })
                }
              ></input>
              <label>City</label>
              <input
                onChange={(e) =>
                  setInfo({
                    ...info,
                    city: e.target.value,
                  })
                }
              ></input>
              <label>From</label>
              <input
                onChange={(e) =>
                  setInfo({
                    ...info,
                    from: e.target.value,
                  })
                }
              ></input>
              <label>Relationship</label>
              <input
                onChange={(e) =>
                  setInfo({
                    ...info,
                    relationship: e.target.value,
                  })
                }
              ></input>
            </div>
          )}
        </label>
      </div>
      <div className='uploadInput'>
        {!password ? (
          <label onClick={() => setPassword(true)}>Change password</label>
        ) : (
          <input
            onChange={(e) =>
              setNewUser({
                ...newUser,
                userId: user._id,
                password: e.target.value,
              })
            }
            type='password'
          ></input>
        )}
      </div>

      <button
        disabled={newUser.password?.length <= 4}
        type='submit'
        className='submitButton'
      >
        Save changes
      </button>
    </form>
  );
}

export default ProfileForm;
