import axios from 'axios';
import { useState } from 'react';
import './profileForm.css';

function ProfileForm({ user }) {
  const [password, setPassword] = useState(null);
  const [profilePic, setProfilePic] = useState('');
  const [coverPic, setCoverPic] = useState('');
  const [error, setError] = useState('Enter password to save changes');

  const newUser = {
    userId: user._id,
    password: password,
    coverPicture: '',
    profilePicture: '',
  };

  const uploadProfile = async (e) => {
    e.preventDefault();

    const data = new FormData();
    const fileName = Date.now() + profilePic.name;
    data.append('name', fileName);
    data.append('file', profilePic);
    newUser.profilePicture = fileName;
    try {
      await axios.post('/upload', data);
    } catch (err) {}
  };

  const uploadCover = async (e) => {
    e.preventDefault();

    const data = new FormData();
    const fileName = Date.now() + coverPic.name;
    data.append('name', fileName);
    data.append('file', coverPic);
    newUser.coverPicture = fileName;
    try {
      await axios.post('/upload', data);
    } catch (err) {}
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      await axios.put('/users/' + user._id, newUser);
    } catch (error) {}
  };

  return (
    <form onSubmit={(e) => submitHandler(e)}>
      <div className='uploadInput'>
        <label>
          <span>
            {!profilePic ? (
              'Change profile picture'
            ) : (
              <button onClick={(e) => uploadProfile(e)} type='button'>
                upload
              </button>
            )}
          </span>
          <input
            name='cover'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setProfilePic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>
          <span>
            {!coverPic ? (
              'Change cover picture'
            ) : (
              <button onClick={(e) => uploadCover(e)} type='button'>
                upload
              </button>
            )}
          </span>
          <input
            name='profile'
            type='file'
            className='upload'
            accept='.png, .jpeg, .jpg, .gif'
            onChange={(e) => {
              setCoverPic(e.target.files[0]);
            }}
          />
        </label>
      </div>
      <div className='uploadInput'>
        <label>Change information</label>
      </div>
      <div className='uploadInput'>
        <label id='passwordLabel'>{error}</label>
        <input
          onChange={(e) => setPassword(e.target.value)}
          type='password'
        ></input>
      </div>

      <button type='submit' className='submitButton'>
        Save changes
      </button>
    </form>
  );
}

export default ProfileForm;
